import React from 'react';
import { Link } from 'react-router-dom';
import './About.css'; // For any custom animations or global overrides

// FeatureCard Component: Reusable component for displaying individual features
// This component displays an icon, title, and description for each feature.
const FeatureCard = ({ icon, title, description }) => {
  return (
    // Styling with Tailwind CSS for a card-like appearance, hover effects, and centering content.
    <div className="bg-white p-6 rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1 flex flex-col items-center text-center">
      {/* Icon for visual appeal, styled with large text and indigo color */}
      <div className="text-4xl mb-4 text-indigo-500">{icon}</div>
      {/* Feature title with bold text and gray color */}
      <h3 className="text-xl font-semibold text-gray-800 mb-2">{title}</h3>
      {/* Feature description with relaxed line height and gray text */}
      <p className="text-gray-600 leading-relaxed">{description}</p>
    </div>
  );
};

// TechBadge Component: Reusable component for displaying technology badges
// This component displays the name of a technology with a specified background color.
const TechBadge = ({ name, colorClass }) => {
  return (
    // Styling with Tailwind CSS for an inline, rounded badge with text and shadow.
    <span className={`inline-flex items-center px-4 py-2 rounded-full text-sm font-medium ${colorClass} text-white shadow-md`}>
      {name}
    </span>
  );
};

// Main About Component
const About = () => {
  return (
    // Main container for the About page.
    // Uses a gradient background consistent with the dashboard, full screen height, and padding.
    // Sets the 'Inter' font globally for this component.
    // Uses flexbox to center the content vertically and horizontally.
    <div className="min-h-screen bg-gradient-to-br from-purple-100 to-indigo-200 p-6 sm:p-8 md:p-12 font-inter flex items-center justify-center">
      {/* Tailwind CSS CDN for styling. This script should ideally be in your public/index.html or main App.js for global use. */}
      <script src="https://cdn.tailwindcss.com"></script>
      {/* Google Fonts for 'Inter'. This link should ideally be in your public/index.html for global use. */}
      <link href="https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap" rel="stylesheet" />

      {/* Content wrapper for the About page.
          Centered horizontally, with max-width, rounded corners, shadow, and internal padding.
          Text is centered within this block. */}
      <div className="max-w-4xl mx-auto bg-white rounded-3xl shadow-2xl p-6 sm:p-8 md:p-10 lg:p-12 text-center">
        {/* Hero section for the About page, containing the main title and introductory paragraphs. */}
        <div className="mb-10 md:mb-14">
          {/* Main heading for the About page, styled with large, bold text and indigo color. */}
          <h1 className="text-5xl md:text-6xl font-extrabold text-indigo-800 mb-4 leading-tight">
            About TaskFlow
          </h1>
          {/* Subheading with a friendly tone. */}
          <p className="text-xl md:text-2xl text-gray-700 font-medium max-w-2xl mx-auto">
            Your intuitive partner for seamless task management.
          </p>
          {/* Detailed description of the application's tech stack.
              Specific technologies are highlighted with bold text and distinct colors. */}
          <p className="text-lg md:text-xl text-gray-600 mt-4 max-w-2xl mx-auto">
            This is a modern task management application with its <span className="font-bold text-indigo-700">frontend built using React</span>, a powerful JavaScript library for user interfaces. The robust <span className="font-bold text-green-700">backend is powered by Spring</span>, a comprehensive framework for Java development. The entire project is seamlessly <span className="font-bold text-purple-700">deployed using Docker</span>, ensuring consistent environments and easy scalability.
          </p>
        </div>

        {/* Features section, displaying a grid of FeatureCard components. */}
        <div className="mb-10 md:mb-14">
          {/* Section heading for features. */}
          <h2 className="text-4xl font-bold text-gray-800 mb-8">Key Features</h2>
          {/* Grid layout for feature cards, adapting to different screen sizes. */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {/* Individual FeatureCard components with icons, titles, and descriptions. */}
            <FeatureCard 
              icon="✏️" 
              title="Task Lifecycle" 
              description="Effortlessly create, edit, and delete tasks to keep your workflow organized." 
            />
            <FeatureCard 
              icon="🔍" 
              title="Smart Search & Filter" 
              description="Quickly find tasks with powerful search and filtering capabilities." 
            />
            <FeatureCard 
              icon="📊" 
              title="Status Tracking" 
              description="Monitor task progress with clear status indicators: Initiated, In Progress, Completed." 
            />
            <FeatureCard 
              icon="🤝" 
              title="Assignee Management" 
              description="Assign tasks to team members and keep track of responsibilities." 
            />
            <FeatureCard 
              icon="📅" 
              title="Due Date Tracking" 
              description="Never miss a deadline with intuitive due date management." 
            />
            <FeatureCard 
              icon="🏷️" 
              title="Category Organization" 
              description="Categorize tasks for better organization and easier navigation." 
            />
          </div>
        </div>

        {/* Tech Stack section, displaying a flexible row of TechBadge components. */}
        <div className="mb-10 md:mb-14">
          {/* Section heading for the tech stack. */}
          <h2 className="text-4xl font-bold text-gray-800 mb-8">Built With Modern Technologies</h2>
          {/* Flexbox layout for tech badges, allowing them to wrap on smaller screens. */}
          <div className="flex flex-wrap justify-center items-center gap-4">
            {/* Individual TechBadge components with names and specific background colors. */}
            <TechBadge name="React" colorClass="bg-blue-500" />
            <TechBadge name="Spring" colorClass="bg-green-600" />
            <TechBadge name="Docker" colorClass="bg-indigo-600" />
            <TechBadge name="React Router" colorClass="bg-red-500" />
            <TechBadge name="Tailwind CSS" colorClass="bg-teal-500" />
            <TechBadge name="JavaScript ES6+" colorClass="bg-yellow-500" />
          </div>
        </div>

        {/* Call to Action section with a button linking to the dashboard. */}
        <div className="mt-10">
          {/* Link styled as a prominent action button, consistent with the dashboard. */}
          <Link 
            to="/dashboard" // Link to your dashboard or tasks page
            className="action-button bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-4 px-8 rounded-full shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1 text-lg sm:text-xl"
          >
            Start Managing Your Tasks!
          </Link>
        </div>
      </div>
    </div>
  );
};

export default About;
