import React, { useState, useEffect, useMemo } from 'react';
import APIService from '../services/api';
import './ActivityFeed.css';

/**
 * Drop this <CommentsButton onOpen={openComments} /> next to your other action buttons.
 * Example (outside this file):
 *   <div className="task-actions">
 *     <Button>Edit</Button>
 *     <Button>Delete</Button>
 *     <CommentsButton onOpen={openComments} count={commentCount} />
 *   </div>
 *
 * This file exports:
 *   - default ActivityFeed (contains the slide-in comments panel)
 *   - CommentsButton (a single button you can place with your other buttons)
 */

export const CommentsButton = ({ onOpen, count = 0 }) => {
  return (
    <button
      type="button"
      className="task-btn comments-btn"
      onClick={onOpen}
      title="Open comments"
      aria-label="Open comments panel"
    >
      ðŸ’¬ Comments{count > 0 ? ` (${count})` : ''}
    </button>
  );
};

const ActivityFeed = ({ task }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [loadingComments, setLoadingComments] = useState(false);
  const [isOpen, setIsOpen] = useState(false);

  // public handlers so the parent can wire the CommentsButton beside other buttons
  const openComments = () => setIsOpen(true);
  const closeComments = () => setIsOpen(false);

  // expose count via memo (use with <CommentsButton count={commentCount} />)
  const commentCount = useMemo(() => comments.length, [comments]);

  // Load comments when panel opens (and task is known) or task changes while open
  useEffect(() => {
    if (!(isOpen && task?.id)) return;
    setLoadingComments(true);
    APIService.getCommentsForTask(task.id)
      .then((res) => {
        if (res?.success) {
          const list = Array.isArray(res.data) ? res.data : [];
          setComments(list.map(normalizeComment));
        }
      })
      .finally(() => setLoadingComments(false));
  }, [isOpen, task]);

  const normalizeComment = (c) => ({
    id: c.id ?? c._id ?? `${(c.userName || c.user_name || 'user')}-${c.created_at || c.create_at || c.createdAt || Date.now()}`,
    userName: c.userName || c.user_name || 'Unknown',
    comment: c.comment || '',
    createdAt: c.created_at || c.create_at || c.createdAt || null,
  });

  const handlePostComment = async () => {
    if (!newComment.trim()) return;

    if (!task?.assignee || task.assignee === 'None') {
      alert('Please select an Assignee for the task before commenting.');
      return;
    }

    const res = await APIService.createComment(task.id, {
      userName: task.assignee,
      comment: newComment.trim(),
    });

    if (res?.success) {
      const normalized = normalizeComment({
        ...(res.data || {}),
        created_at: res?.data?.created_at || res?.data?.create_at || new Date().toISOString(),
      });
      setComments((prev) => [...prev, normalized]);
      setNewComment('');
    } else {
      alert(`Failed to post comment: ${res?.error || 'Unknown error'}`);
    }
  };

  const formatCommentTime = (dateString) => {
    if (!dateString) return { timeOnly: '', full: '' };
    const date = new Date(dateString);
    return {
      timeOnly: date.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: true }),
      full: date.toLocaleString(),
    };
  };

  /* 
    IMPORTANT:
    - Do NOT render any extra action buttons here.
    - Only the CommentsButton is added (exported) so you can place it
      alongside your existing buttons in your page layout.
  */

  return (
    <>
      {/* Slide-in comments panel */}
      <aside
        className={`activity-feed-panel ${isOpen ? 'open' : ''}`}
        aria-hidden={!isOpen}
        aria-label="Comments panel"
      >
        <div className="activity-feed-header">
          <h4>Comments</h4>
          <button
            type="button"
            className="close-panel-btn"
            onClick={closeComments}
            aria-label="Close comments panel"
            title="Close"
          >
            Ã—
          </button>
        </div>

        <div className="activity-feed-body">
          <div className="comment-input-area">
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="Add comment"
            />
            <button onClick={handlePostComment} className="post-comment-btn" title="Post comment">
              <span>&gt;</span>
            </button>
          </div>

          <div className="comment-list">
            {loadingComments ? (
              <p className="loading">Loading comments...</p>
            ) : (
              comments.map((c) => {
                const { timeOnly, full } = formatCommentTime(c.createdAt);
                const initials = (c.userName || '??')
                  .toString()
                  .trim()
                  .split(/\s+/)
                  .map((w) => w[0])
                  .slice(0, 2)
                  .join('')
                  .toUpperCase();

                return (
                  <div key={c.id} className="comment-item">
                    <div className="comment-avatar" aria-hidden="true">{initials || '??'}</div>
                    <div className="comment-content">
                      <div className="comment-header">
                        <span className="comment-author">{c.userName}</span>
                        <span className="comment-time" title={full}>{timeOnly}</span>
                      </div>
                      <p className="comment-text">{c.comment}</p>
                    </div>
                  </div>
                );
              })
            )}
            {!loadingComments && comments.length === 0 && (
              <p className="empty-state">No comments yet. Be the first to add one!</p>
            )}
          </div>
        </div>
      </aside>

      {isOpen && <button className="activity-feed-backdrop" aria-label="Close comments" onClick={closeComments} />}

      {/* Hidden helper to let the parent grab handlers & count without adding UI here */}
      <div
        style={{ display: 'none' }}
        data-comments-count={commentCount}
        data-comments-open={openComments}
      />
    </>
  );
};

export default ActivityFeed;
