import React, { useState, useEffect, useMemo } from 'react';
import APIService from '../services/api';
import './ActivityFeed.css';

/**
 * ActivityFeed
 * - Renders a floating "Comments" button.
 * - Clicking it opens a slide-in sidebar from the right.
 * - Shows existing comments and lets users add a new one.
 * - Displays the time a comment was created (hh:mm AM/PM).
 */
const ActivityFeed = ({ task }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [loadingComments, setLoadingComments] = useState(false);
  const [isOpen, setIsOpen] = useState(false);

  // A safe way to read common API field variations
  const normalizeComment = (c) => ({
    id: c.id ?? c._id ?? `${(c.userName || c.user_name || 'user')}-${(c.created_at || c.create_at || c.createdAt || c.updated_at || Date.now())}`,
    userName: c.userName || c.user_name || c.user || 'Unknown',
    comment: c.comment || c.text || '',
    createdAt: c.created_at || c.create_at || c.createdAt || c.created || null,
  });

  // precomputed count for the button
  const commentCount = useMemo(() => comments.length, [comments]);

  // Load comments when:
  // 1) the panel opens AND we have a task id
  // 2) the task changes while panel is already open
  useEffect(() => {
    const shouldLoad = isOpen && task && task.id;
    if (!shouldLoad) return;

    setLoadingComments(true);
    APIService.getCommentsForTask(task.id).then((response) => {
      if (response?.success) {
        const list = Array.isArray(response.data) ? response.data : [];
        setComments(list.map(normalizeComment));
      }
    }).finally(() => setLoadingComments(false));
  }, [isOpen, task]);

  const handlePostComment = async () => {
    if (!newComment.trim()) return;

    if (!task?.assignee || task.assignee === 'None') {
      alert('Please select an Assignee for the task before commenting.');
      return;
    }

    const commentData = {
      userName: task.assignee,
      comment: newComment.trim(),
    };

    const response = await APIService.createComment(task.id, commentData);
    if (response?.success) {
      // Ensure the newly created comment shows a created time even if the API doesn't return one
      const normalized = normalizeComment({
        ...(response.data || {}),
        created_at: response?.data?.created_at || response?.data?.create_at || new Date().toISOString(),
      });
      setComments((prev) => [...prev, normalized]);
      setNewComment('');
    } else {
      alert(`Failed to post comment: ${response?.error || 'Unknown error'}`);
    }
  };

  const formatCommentTime = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    // Time (shown) and full datetime (title/tooltip)
    return {
      timeOnly: date.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: true }),
      full: date.toLocaleString(),
    };
  };

  return (
    <>
      {/* Floating toggle button */}
      <button
        type="button"
        className="comments-toggle-btn"
        aria-expanded={isOpen}
        aria-controls="activity-feed-panel"
        onClick={() => setIsOpen(true)}
        title="Open comments"
      >
        ðŸ’¬ Comments{commentCount ? ` (${commentCount})` : ''}
      </button>

      {/* Slide-in panel */}
      <aside
        id="activity-feed-panel"
        className={`activity-feed-panel ${isOpen ? 'open' : ''}`}
        aria-hidden={!isOpen}
      >
        <div className="activity-feed-header">
          <h4>Comments</h4>
          <button
            type="button"
            className="close-panel-btn"
            onClick={() => setIsOpen(false)}
            aria-label="Close comments panel"
            title="Close"
          >
            Ã—
          </button>
        </div>

        <div className="activity-feed-body">
          <div className="comment-input-area">
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="Add comment"
            />
            <button onClick={handlePostComment} className="post-comment-btn" title="Post comment">
              <span>&gt;</span>
            </button>
          </div>

          <div className="comment-list">
            {loadingComments ? (
              <p className="loading">Loading comments...</p>
            ) : (
              comments.map((comment) => {
                const { timeOnly, full } = formatCommentTime(comment.createdAt);
                const initials = (comment.userName || '??')
                  .toString()
                  .trim()
                  .split(/\s+/)
                  .map((w) => w[0])
                  .slice(0, 2)
                  .join('')
                  .toUpperCase();

                return (
                  <div key={comment.id} className="comment-item">
                    <div className="comment-avatar" aria-hidden="true">{initials || '??'}</div>
                    <div className="comment-content">
                      <div className="comment-header">
                        <span className="comment-author">{comment.userName}</span>
                        <span className="comment-time" title={full}>{timeOnly}</span>
                      </div>
                      <p className="comment-text">{comment.comment}</p>
                    </div>
                  </div>
                );
              })
            )}
            {!loadingComments && comments.length === 0 && (
              <p className="empty-state">No comments yet. Be the first to add one!</p>
            )}
          </div>
        </div>
      </aside>

      {/* Backdrop to close panel on click */}
      {isOpen && <button className="activity-feed-backdrop" aria-label="Close comments" onClick={() => setIsOpen(false)} />}
    </>
  );
};

export default ActivityFeed;
