import React, { useState, useEffect, useMemo } from 'react';
import APIService from '../services/api';
import './ActivityFeed.css';

const ActivityFeed = ({ task }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [loadingComments, setLoadingComments] = useState(false);
  const [isOpen, setIsOpen] = useState(false);

  const normalizeComment = (c) => ({
    id: c.id ?? c._id ?? Math.random(),
    userName: c.userName || c.user_name || 'Unknown',
    comment: c.comment || '',
    createdAt: c.created_at || c.create_at || c.createdAt || null,
  });

  const commentCount = useMemo(() => comments.length, [comments]);

  useEffect(() => {
    if (!(isOpen && task?.id)) return;
    setLoadingComments(true);
    APIService.getCommentsForTask(task.id).then((res) => {
      if (res?.success) {
        setComments((res.data || []).map(normalizeComment));
      }
    }).finally(() => setLoadingComments(false));
  }, [isOpen, task]);

  const handlePostComment = async () => {
    if (!newComment.trim()) return;

    if (!task?.assignee || task.assignee === 'None') {
      alert('Please select an Assignee before commenting.');
      return;
    }

    const commentData = { userName: task.assignee, comment: newComment.trim() };
    const res = await APIService.createComment(task.id, commentData);
    if (res?.success) {
      const normalized = normalizeComment({
        ...(res.data || {}),
        created_at: res.data?.created_at || new Date().toISOString(),
      });
      setComments((prev) => [...prev, normalized]);
      setNewComment('');
    }
  };

  const formatCommentTime = (dateString) => {
    if (!dateString) return '';
    const d = new Date(dateString);
    return {
      short: d.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
      full: d.toLocaleString(),
    };
  };

  return (
    <>
      {/* Example task actions row */}
      <div className="task-actions">
        <button className="task-btn">Edit</button>
        <button className="task-btn">Delete</button>
        <button className="task-btn">Assign</button>
        <button
          className="task-btn comments-btn"
          onClick={() => setIsOpen(true)}
        >
          ðŸ’¬ Comments {commentCount > 0 && `(${commentCount})`}
        </button>
      </div>

      {/* Slide-in panel */}
      <aside className={`activity-feed-panel ${isOpen ? 'open' : ''}`}>
        <div className="activity-feed-header">
          <h4>Comments</h4>
          <button
            className="close-panel-btn"
            onClick={() => setIsOpen(false)}
          >
            Ã—
          </button>
        </div>

        <div className="activity-feed-body">
          <div className="comment-input-area">
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="Add comment"
            />
            <button onClick={handlePostComment} className="post-comment-btn">
              <span>&gt;</span>
            </button>
          </div>

          <div className="comment-list">
            {loadingComments ? (
              <p>Loading comments...</p>
            ) : (
              comments.map((c) => {
                const { short, full } = formatCommentTime(c.createdAt);
                return (
                  <div key={c.id} className="comment-item">
                    <div className="comment-avatar">
                      {c.userName?.substring(0, 2).toUpperCase()}
                    </div>
                    <div className="comment-content">
                      <div className="comment-header">
                        <span className="comment-author">{c.userName}</span>
                        <span className="comment-time" title={full}>{short}</span>
                      </div>
                      <p className="comment-text">{c.comment}</p>
                    </div>
                  </div>
                );
              })
            )}
          </div>
        </div>
      </aside>

      {isOpen && <div className="activity-feed-backdrop" onClick={() => setIsOpen(false)} />}
    </>
  );
};

export default ActivityFeed;
