import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import APIService from '../services/api'; // Assuming this path is correct
import './Dashboard.css'; // For custom animations and global styles

// StatCard Component: Reusable component for displaying individual statistics
const StatCard = ({ title, value, isLoading, icon }) => {
  return (
    <div className="bg-white p-6 rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1 flex flex-col items-center justify-center text-center">
      {/* Icon for visual appeal */}
      <div className="text-4xl mb-3 text-indigo-500">
        {icon}
      </div>
      <h3 className="text-sm font-semibold uppercase tracking-wide text-gray-600 mb-2">{title}</h3>
      {isLoading ? (
        // Skeleton loader for a smoother loading experience
        <div className="h-10 w-24 bg-gray-200 rounded-md animate-pulse"></div>
      ) : (
        <p className="text-5xl font-extrabold text-indigo-700">{value}</p>
      )}
    </div>
  );
};

const Dashboard = () => {
  const [stats, setStats] = useState({
    total: 0,
    inProgress: 0,
    completed: 0,
    initiated: 0
  });
  const [loading, setLoading] = useState(true);

  // useEffect to fetch stats on component mount
  useEffect(() => {
    fetchStats();
  }, []);

  // Function to fetch task statistics from the API
  const fetchStats = async () => {
    try {
      setLoading(true); // Set loading to true before fetching
      const response = await APIService.getAllTasks(); // Call your API service
      if (response.success) {
        const tasks = response.data;
        // Calculate statistics based on task status
        const newStats = {
          total: tasks.length,
          inProgress: tasks.filter(task => task.status === 'In Progress').length,
          completed: tasks.filter(task => task.status === 'Completed').length,
          initiated: tasks.filter(task => task.status === 'Initiated').length
        };
        setStats(newStats); // Update state with new statistics
      } else {
        console.error('API response not successful:', response);
      }
    } catch (err) {
      console.error('Failed to fetch stats:', err); // Log any errors
    } finally {
      setLoading(false); // Set loading to false after fetch completes (success or error)
    }
  };

  return (
    // Main dashboard container with a vibrant background gradient and generous padding
    <div className="min-h-screen bg-gradient-to-br from-purple-100 to-indigo-200 p-6 sm:p-8 md:p-12 font-inter">
      {/* Tailwind CSS CDN for styling */}
      <script src="https://cdn.tailwindcss.com"></script>
      {/* Google Fonts for 'Inter' */}
      <link href="https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap" rel="stylesheet" />

      {/* Dashboard content wrapper, centered and with max-width */}
      <div className="max-w-6xl mx-auto bg-white rounded-3xl shadow-2xl p-6 sm:p-8 md:p-10 lg:p-12">
        {/* Header section with a friendly tone */}
        <div className="text-center mb-10 md:mb-14">
          <h1 className="text-5xl md:text-6xl font-extrabold text-indigo-800 mb-4 leading-tight">
            Your Task Hub!
          </h1>
          <p className="text-xl md:text-2xl text-gray-700 font-medium">
            Let's get things done, shall we?
          </p>
        </div>

        {/* Statistics section using a responsive grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 md:gap-8 mb-10 md:mb-14">
          <StatCard title="Total Tasks" value={stats.total} isLoading={loading} icon="📋" /> {/* Clipboard icon */}
          <StatCard title="In Progress" value={stats.inProgress} isLoading={loading} icon="⏳" /> {/* Hourglass icon */}
          <StatCard title="Completed" value={stats.completed} isLoading={loading} icon="✅" /> {/* Checkmark icon */}
          <StatCard title="Initiated" value={stats.initiated} isLoading={loading} icon="🚀" /> {/* Rocket icon */}
        </div>

        {/* Action buttons section */}
        <div className="flex flex-col sm:flex-row justify-center items-center gap-4 md:gap-6">
          {/* Link to view all tasks with a playful button style */}
          <Link 
            to="/tasks" 
            className="action-button bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-4 px-8 rounded-full shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1 text-lg sm:text-xl"
          >
            See All Tasks
          </Link>
          {/* Button to create a new task (assuming a route exists) */}
          <Link 
            to="/create-task" // Assuming you have a route for creating tasks
            className="action-button bg-green-500 hover:bg-green-600 text-white font-bold py-4 px-8 rounded-full shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1 text-lg sm:text-xl"
          >
            Add New Task
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
