import React from 'react';
import { Link } from 'react-router-dom';
import './About.css';

const FeatureCard = ({ icon, title, description }) => (
  <div className="feature-card">
    <div className="icon">{icon}</div>
    <h3>{title}</h3>
    <p>{description}</p>
  </div>
);

const About = () => {
  return (
    <div className="about-container">
      <section className="about-hero">
        <h1>Meet <span>TaskFlow</span></h1>
        <p>A refined way to manage your workflow like a pro.</p>
        <p className="stack-desc">
          Powered by <strong>React</strong> on the frontend, <strong>Spring Boot</strong> on the backend, and fully containerized with <strong>Docker</strong>.
        </p>
        <Link to="/dashboard" className="cta-button">
          Let’s Go 🚀
        </Link>
      </section>

      <section className="features-scroll">
        <FeatureCard icon="✅" title="Quick Actions" description="Instantly create, assign, and manage tasks." />
        <FeatureCard icon="🧠" title="Smart Suggestions" description="Get auto-sorted priorities based on status." />
        <FeatureCard icon="📈" title="Progress Insights" description="Visual status tracking at a glance." />
        <FeatureCard icon="🗂️" title="Organized Categories" description="Group tasks by type or context easily." />
        <FeatureCard icon="⏰" title="Deadline Alerts" description="Smart reminders keep you on track." />
      </section>

      <section className="tech-bubbles">
        <span className="bubble react">React</span>
        <span className="bubble spring">Spring</span>
        <span className="bubble docker">Docker</span>
        <span className="bubble tailwind">Tailwind</span>
        <span className="bubble router">React Router</span>
      </section>
    </div>
  );
};

export default About;
